#import "ZCheckmarkImages.h"
#import "NSData+Base64.h"

#define CHECKMARK @""

#define CHECKMARK_2X @""

#define UNCHECKMARK @""

#define UNCHECKMARK_2X @""

@implementation ZCheckmarkImages

+ (UIImage *)image1xScale:(NSString *)base64_1x and2xScale:(NSString *)base64_2x{
    CGFloat screenScale = [[UIScreen mainScreen] scale];
    NSData *imageData = [NSData dataFromBase64String:screenScale == 1.0f ? base64_1x : base64_2x];
    return [UIImage imageWithData:imageData scale:screenScale];
}

+ (UIImage *)checkmark{
    return [ZCheckmarkImages image1xScale:CHECKMARK and2xScale:CHECKMARK_2X];
}

+ (UIImage *)uncheckedmark{
    return [ZCheckmarkImages image1xScale:UNCHECKMARK and2xScale:UNCHECKMARK_2X];
}

@end
